            ‚Üê All posts
          </Link>
          {/* <Link className={style["btn"]} to={`/blogs/edit/${id}`}>
            Edit
          </Link> */}
          <button className={style["delete"]} onClick={() => setConfirm(true)}>
            Delete
          </button>
        </div>
      </div>
    </>
  );
};

export default ContactDetail;
